import React, { useState } from "react";
import axios from "axios";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1); // Step 1: Login, Step 2: OTP
  const [message, setMessage] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3001/login", { email, password });
      setMessage(response.data.message);
      setStep(2); // Move to OTP step
    } catch (error) {
      setMessage(error.response?.data?.error || "Login failed.");
    }
  };

  const handleOtp = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3001/verify-otp", { email, otp });
      setMessage(`Welcome, ${response.data.userDetails.name}!`);
    } catch (error) {
      setMessage(error.response?.data?.error || "OTP verification failed.");
    }
  };

  return (
    <div>
      {step === 1 ? (
        <form onSubmit={handleLogin}>
          <h2>Login</h2>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit">Login</button>
        </form>
      ) : (
        <form onSubmit={handleOtp}>
          <h2>Enter OTP</h2>
          <input type="text" placeholder="OTP" value={otp} onChange={(e) => setOtp(e.target.value)} required />
          <button type="submit">Verify OTP</button>
        </form>
      )}
      <p>{message}</p>
    </div>
  );
}

export default Login;
