import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Backend call to validate credentials
    try {
      const response = await fetch('https://example.com/api/validate-login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        // Generate OTP
        const otpResponse = await fetch('https://example.com/api/generate-otp', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email }),
        });

        if (otpResponse.ok) {
          const { otp } = await otpResponse.json();
          setOtp(otp);
          setOtpSent(true);
          setError('');
          alert(`OTP sent to your email. OTP: ${otp}`); // For demo purposes
        } else {
          setError('Failed to generate OTP. Please try again.');
        }
      } else {
        setError('Invalid email or password');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again.');
    }
  };

  const handleOtpVerification = (e) => {
    e.preventDefault();

    // Validate OTP
    if (otp === e.target.otp.value) {
      setOtpVerified(true);
      alert('OTP verified successfully!');
      window.location.href = `/thankyou?email=${encodeURIComponent(email)}`;
    } else {
      setError('Invalid OTP. Please try again.');
    }
  };

  if (otpVerified) {
    return null; // Redirect handled above
  }

  return (
    <div className="login-container">
      {!otpSent ? (
        <form onSubmit={handleLogin} className="login-form">
          <h2>Login</h2>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit">Login</button>
        </form>
      ) : (
        <form onSubmit={handleOtpVerification} className="otp-form">
          <h2>Enter OTP</h2>
          <div className="input-group">
            <label htmlFor="otp">OTP</label>
            <input
              type="text"
              id="otp"
              name="otp"
              placeholder="Enter the OTP"
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit">Verify OTP</button>
        </form>
      )}
    </div>
  );
};

export default Login;
