import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [generatedOtp, setGeneratedOtp] = useState(null);
  const [otpVerified, setOtpVerified] = useState(false);
  const [error, setError] = useState('');
  const [step, setStep] = useState(1); // 1: Login, 2: OTP, 3: Thank You, 4: Error

  // Simulate database user validation
  const validateCredentials = (email, password) => {
    return email === 'user@example.com' && password === 'password123';
  };

  // Generate 6-digit OTP
  const generateOtp = () => {
    return Math.floor(100000 + Math.random() * 900000).toString();
  };

  const handleLogin = (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    if (validateCredentials(email, password)) {
      const otp = generateOtp();
      setGeneratedOtp(otp);
      console.log(`Generated OTP: ${otp}`); // Display OTP in console for testing

      setTimeout(() => setGeneratedOtp(null), 10 * 60 * 1000); // OTP expires in 10 minutes
      setError('');
      setStep(2); // Move to OTP verification step
    } else {
      setStep(4); // Redirect to error page
    }
  };

  const handleOtpVerification = (e) => {
    e.preventDefault();

    if (otp === generatedOtp) {
      setOtpVerified(true);
      setStep(3); // Move to Thank You page
    } else {
      setError('Invalid OTP');
    }
  };

  return (
    <div className="login-container">
      {step === 1 && (
        <form onSubmit={handleLogin} className="login-form">
          <h2>Login</h2>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit">Login</button>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleOtpVerification} className="otp-form">
          <h2>Enter OTP</h2>
          <p>An OTP has been sent to your email. Please enter it below:</p>
          <div className="input-group">
            <label htmlFor="otp">OTP</label>
            <input
              type="text"
              id="otp"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              placeholder="Enter OTP"
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit">Verify OTP</button>
        </form>
      )}

      {step === 3 && otpVerified && (
        <div className="thank-you">
          <h2>Thank You!</h2>
          <p>Welcome, {email}!</p>
          <p>Your account details have been successfully verified.</p>
        </div>
      )}

      {step === 4 && (
        <div className="error-page">
          <h2>Error</h2>
          <p>Sorry, we can't log you in.</p>
        </div>
      )}
    </div>
  );
};

export default Login;
