import React from 'react';

const ThankYou = () => {
  const handleRemoveAccount = () => {
    // Example: Call a backend API to remove the account
    const email = localStorage.getItem('userEmail'); // Assuming email is stored locally
    fetch('https://example.com/api/remove-account', {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email }),
    })
      .then((response) => {
        if (response.ok) {
          alert('Your account has been removed successfully.');
          localStorage.removeItem('userEmail'); // Clear stored data
          // Redirect to the login page or homepage
          window.location.href = '/login';
        } else {
          alert('Failed to remove account. Please try again.');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('An error occurred while removing your account.');
      });
  };

  return (
    <div className="thank-you">
      <h2>Thank You!</h2>
      <p>Your registration is complete. You can now log in.</p>
      <button onClick={handleRemoveAccount} className="remove-account-btn">
        Remove Account
      </button>
    </div>
  );
};

export default ThankYou;
